import './App.css';
import React, { useState } from 'react';
import passwordDisplayer from './components/passwordDisplayer';
//import passwordGenerator from '../components/passwordGenerator.jsx';

const generatePassword = (length, params) => {
  const password = [];
  const numbers = '0123456789';
  const symbols = '!@#$%^&*()_+~`|}{[]:;?><,./-=';
  const lowerCase = 'abcdefghijklmnopqrstuvwxyz';
  const upperCase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  let generatedPassword = '';

  if (params.numbers) password.push(...numbers);
  if (params.symbols) password.push(...symbols);
  if (params.lowerCase) password.push(...lowerCase);
  if (params.upperCase) password.push(...upperCase);
  for (let i = 0; i < length; i++) {
    generatedPassword += password[Math.floor(Math.random() * password.length)];
  }
  return generatedPassword;
}

function App() {
  const [password, setPassword] = useState('');
  const [requiredLength, setRequireLength] = useState(10);
  const [loading, setLoading] = useState(true);
  const [params, setParams] = useState({
    numbers: true, symbols: true,
    lowerCase: true, upperCase: true,
  });

  const passwordGenerator = (length, params) => {
    setRequireLength(parseInt(length));
    setPassword(generatePassword(parseInt(length), params));
  }

  const handleChangeCheckbox = (field, value) => {
    let tmpParams = params
    tmpParams[field] = !tmpParams[field]
    setParams(tmpParams)
    setPassword(generatePassword(requiredLength, tmpParams));
  }

  const copyToClipboard = () => {
    navigator.clipboard.writeText(password);
  }

  if (loading) {
    setPassword(generatePassword(requiredLength, params));
    setLoading(false);
  }

  return (
    <div className="App-style">
      <div className="Password-container">
        <div className="Text-container">
          {
            password.length === 0 ?
              <p className='Password-default-text'>P4$5W0rD</p> :
              <p className='Password-edited-text'>{password}</p>
          }
        </div>
        <button className='Password-button' onClick={copyToClipboard}>Copy</button>
      </div>
      <div className="Input-container">
        <div className='Password-default-text'>Character length: {requiredLength}</div>
        <input min="0" max="20" value={requiredLength} onChange={(val) => passwordGenerator(val.target.value, params)} type="range" />
        <div className='Password-default-text'>
          <input type="checkbox" checked={params.lowerCase} onChange={(val) => handleChangeCheckbox("lowerCase", val.target.value)} /> Include Lowercase Letters
        </div>
        <div className='Password-default-text'>
          <input type="checkbox" checked={params.upperCase} onChange={(val) => handleChangeCheckbox("upperCase", val.target.value)} /> Include Uppercase Letters
        </div>
        <div className='Password-default-text'>
          <input type="checkbox" checked={params.numbers} onChange={(val) => handleChangeCheckbox("numbers", val.target.value)} /> Include Numbers
        </div>
        <div className='Password-default-text'>
          <input type="checkbox" checked={params.symbols} onChange={(val) => handleChangeCheckbox("symbols", val.target.value)} /> Include Symbols
        </div>
      </div>
    </div>
  );
}

export default App;
